    url = 'https://car.autohome.com.cn/price/series-{}.html#pvareaid=2042205'.format(id)
    # 网站

    browser.get(url)
    print('正在解析的链接:',url)
    # data = browser.find_element_by_xpath('//*[@id="spgzd48232"]').get_attribute('style')
    # print(data)

    # 获取网页源代码
    data = browser.page_source
    # print(data)

    data_page = parsel.Selector(data)

    li_list = data_page.xpath('//ul[@class="interval01-list"]/li')
    for li in li_list:
        try:
            spec_id = li.xpath('./div[@class="interval01-list-attention"]/div/span/@id').get().replace('spgzd','')

            # spec_id = li.xpath('./div[@class="interval01-list-attention"]/div/span/@id').get()
            print('车型id',spec_id)

            attention = li.xpath('./div[@class="interval01-list-attention"]/div/span/@style').get().replace('width:','').replace('%','')
            # style = li.xpath('./div[@class="interval01-list-attention"]/div/span/@style').get()
            print('关注度',attention)
            data = (spec_id,attention)

            updata_sql = "UPDATE t_car_detail SET attention='{}' where spec_id = '{}'".format(data[1], data[0])
            cursor.execute(updata_sql)
            conn.commit()
            print('车型id:',spec_id,'更新关注度:',attention)

        except AttributeError:
            continue
